试验组与对照组的比率都为100%或0%（MN方法）
%macro ratediff(n1=,n1_event=,n2=,n2_event=,alpha=,side=);
data mienur;
n1=&n1.;
a1=&n1_event.;
a2=n1-a1;
p1=a1/n1;
n2=&n2.;
a3=&n2_event.;
a4=n2-a3;
p2=a3/n2;
z=probit(1-&alpha./&side.);
d=p1-p2;
**率差置信区间下限;
**率差置信区间上限;
l_diff=-(z**2*(a1+a3)/((a1+a3-1)*a1))/((z**2*(a1+a3)/((a1+a3-1)*a1))+1);
u_diff= (z**2*(a1+a3)/((a1+a3-1)*a3))/((z**2*(a1+a3)/((a1+a3-1)*a3))+1);
run;
%mend ratediff;

%ratediff(n1=100,n1_event=100,n2=100,n2_event=100,alpha=0.05,side=2);


分层的Miettinen-Nurminen方法计算率差与P值
%macro rrv(c0,c1,s0,s1,z,rd,ra,rb,vv);/*根据rd计算r0和r1*/
 c0=&c0.;
 c1=&c1.;
 s0=&s0.;
 s1=&s1.;
 c=c1+c0;
 s=s1+s0;
 p1=c1/s1;
 p0=c0/s0;
 z2=&z.**2;
 rd=&rd.;

 l3=s;
 l2=(s1+2*s0)*rd-s-c;
 l1=(s0*rd-s-2*c0)*rd+c;
 l0=c0*rd*(1-rd);

 q=l2**3/(3*l3)**3-l1*l2/(6*l3**2)+l0/(2*l3);
 p=sign(q)*(l2**2/(3*l3)**2-l1/(3*l3))**0.5;
 if p=0 then aa=0; 
 else aa=q/p**3;
 if abs(aa)&gt;1 then aa=1;
 a=1/3*(pi+arcos(aa));
 r0=2*p*cos(a)-l2/(3*l3);

 r1=r0+rd;
 v=(r1*(1-r1)/s1+r0*(1-r0)/s0)*s/(s-1);
 &ra.=r0;
 &rb.=r1;
 &vv.=v;
%mend;

%macro stra_mn(dat=,stra=,trt=,c=,n=,alp=,Wmethod=2,all=0,out=);

%if &stra.=0 %then %do;

proc sort data=&dat. out=dat1;
 by &trt.;
run;

data dat1x;
 set dat1 nobs=nob;
 by &trt.;
 array ccc(2);
 array nnn(2);
 retain ccc: nnn:;
 if first.&trt. then do;
    i+1;
    ccc(i)=&c.;
    nnn(i)=&n.;
 end;
 if _N_=nob then do;
    ca=ccc(1);
    cb=ccc(2);
    na=nnn(1);
    nb=nnn(2);
    output;
 end;
 keep ca cb na nb;
run;

data dat2;
 set dat1x;
 z=probit(1-&alp./2);
 pi=constant("pi");
 pa=ca/na;
 pb=cb/nb;

/*计算置信下限*/

 str=0;
 qxq=0;
 do qcyc=1 to 3;

    poit=10**(-qcyc*2);
    lmin=10;
    str=str+qxq*poit*100;

 do cyc=-99 to 99;
    rdlx=str+cyc*poit;
    %rrv(ca,cb,na,nb,z,rdlx,ra,rb,vv);
    if abs(vv)&lt;10**(-12) then vv=0;
    rdx=pb-pa-(z*z*vv)**0.5; /*置信下限*/
    if vv^=0 then diff=abs(rdx-rdlx);
    if diff&lt;lmin then do;
       lmin=diff;
       qxq=cyc;
    end;
    if &all.=1 then output;
 end; 

 end;
 rdl=str+qxq*poit;

 /*计算置信上限*/
 str=0;
 qxq=0;
 do qcyc=1 to 3;

    poit=10**(-qcyc*2);
    dmin=10;
    str=str+qxq*poit*100;

 do cyc=-99 to 99;
    rdlx=str+cyc*poit;
    %rrv(ca,cb,na,nb,z,rdlx,ra,rb,vv);
    if abs(vv)&lt;10**(-12) then vv=0;
    rdx=pb-pa+(z*z*vv)**0.5; /*置信上限*/
    if vv^=0 then diff=abs(rdx-rdlx);
    if diff&lt;dmin then do;
       dmin=diff;
       qxq=cyc;
    end;
    if &all.=1 then output;
 end; 

 end;
 rdu=str+qxq*poit;

/*Z统计量*/

 rdlx=0;
 %rrv(ca,cb,na,nb,z,rdlx,ra,rb,vv);
 if abs(vv)&lt;10**(-16) then Zsc=0;
 else Zsc=-abs((pb-pa)/vv**0.5);
 Pvalue=probnorm(Zsc)*2;

 /*率差*/
 rankdiff=pb-pa;
 output;

 %if &all.=1 %then %do;
     keep diff rdx rdlx rdl ra rb vv rdl dmin rdu lmin str qxq;
 %end;
 %else %do;
     keep rdl rdu dmin lmin Zsc Pvalue rankdiff ;
 %end;
run;

%end;



/**************************************************/
/*分层MN*/
%else %do;

proc sort data=&dat. out=dat1;
 by &stra. &trt.;
run;

data _NULL_;
 set dat1 nobs=nob;
 array cc(99);
 array nn(99);
 retain cc: nn:;
 by &stra. &trt.;
 x+1;
 cc(x)=&c.;
 nn(x)=&n.;
 if _N_=nob then do;
    grp=x/2;
    do i=1 to grp;
       call symputx("c"||strip(put(i,best.))||"_0",strip(put(cc(i*2-1),best.)));
       call symputx("c"||strip(put(i,best.))||"_1",strip(put(cc(i*2),best.)));
       call symputx("n"||strip(put(i,best.))||"_0",strip(put(nn(i*2-1),best.)));
       call symputx("n"||strip(put(i,best.))||"_1",strip(put(nn(i*2),best.)));
    end;
    call symputx("Ntrt",strip(put(x/2,best.)));
 end;
run;

data dat2;
 array ca(&Ntrt.);
 array cb(&Ntrt.);
 array na(&Ntrt.);
 array nb(&Ntrt.);
 array pa(&Ntrt.);
 array pb(&Ntrt.);
 array ra(&Ntrt.);
 array rb(&Ntrt.);
 array vv(&Ntrt.);
 array wfr(&Ntrt.);
 array sw(&Ntrt.);
 ntrt=&Ntrt.;
 %do i=1 %to &Ntrt.;
     ca&i.=&&c&i._0.;
     cb&i.=&&c&i._1.;
     na&i.=&&n&i._0.;
     nb&i.=&&n&i._1.;
 %end;
 z=probit(1-&alp./2);
 pi=constant("pi");
 do i=1 to ntrt;
    pa(i)=ca(i)/na(i);
    pb(i)=cb(i)/nb(i);
 end;

 /*率差*/
 ew=0;
 do i=1 to ntrt;  
    %if &wmethod.=1 %then %do; 
        wfr(i)=(na(i)+nb(i));
    %end;
    %if &wmethod.=2 %then %do; 
        wfr(i)=1/(1/na(i)+1/nb(i));
    %end;
    ew=ew+wfr(i);
 end;
 rra=0;
 rrb=0;
 do i=1 to ntrt;
    sw(i)=wfr(i)/ew;
    rra=rra+sw(i)*pa(i);
    rrb=rrb+sw(i)*pb(i);
 end;
 rankdiff=rrb-rra;

 /*计算置信下限*/
 str=0;
 qxq=0;
 do qcyc=1 to 3;

    poit=10**(-qcyc*2);
    lmin=10;
    str=str+qxq*poit*100;

 do cyc=-99 to 99;
    rdlx=str+cyc*poit;

    do i=1 to ntrt;
       %rrv(ca(i),cb(i),na(i),nb(i),z,rdlx,ra(i),rb(i),vv(i));
       if abs(vv(i))&lt;10**(-12) then vv(i)=0;
    end;

    rra=0;
    rrb=0;
    vxv=0;
    do i=1 to ntrt;
       rra=rra+sw(i)*pa(i);
       rrb=rrb+sw(i)*pb(i);
       vxv=vxv+vv(i)*sw(i)*sw(i);
    end;
    rdx=rrb-rra-(z*z*vxv)**0.5; /*置信下限*/
    diff=abs(rdx-rdlx);
    if diff&lt;lmin then do;
       lmin=diff;
       qxq=cyc;
    end;
    if &all.=1 then output;
 end; 

 end;

 rdl=str+qxq*poit;


 /*计算置信上限*/
 str=0;
 qxq=0;
 do qcyc=1 to 3;

    poit=10**(-qcyc*2);
    dmin=10;
    str=str+qxq*poit*100;

 do cyc=-99 to 99;
    rdlx=str+cyc*poit;

    do i=1 to ntrt;
       %rrv(ca(i),cb(i),na(i),nb(i),z,rdlx,ra(i),rb(i),vv(i));
       if abs(vv(i))&lt;10**(-12) then vv(i)=0;
    end;

    rra=0;
    rrb=0;
    vxv=0;
    do i=1 to ntrt;
       rra=rra+sw(i)*pa(i);
       rrb=rrb+sw(i)*pb(i);
       vxv=vxv+vv(i)*sw(i)*sw(i);
    end;
    rdx=rrb-rra+(z*z*vxv)**0.5; /*置信上限*/
    diff=abs(rdx-rdlx);
    if diff&lt;dmin then do;
       dmin=diff;
       qxq=cyc;
    end;
    if &all.=1 then output;
 end; 

 end;

 rdu=str+qxq*poit;

/*Z统计量*/

 rdlx=0;

 do i=1 to ntrt;
    %rrv(ca(i),cb(i),na(i),nb(i),z,rdlx,ra(i),rb(i),vv(i));
    if abs(vv(i))&lt;10**(-12) then vv(i)=0;
 end;

 rra=0;
 rrb=0;
 vxv=0;
 do i=1 to ntrt;
    rra=rra+sw(i)*pa(i);
    rrb=rrb+sw(i)*pb(i);
    vxv=vxv+vv(i)*sw(i)*sw(i);
 end;
 Zsc=-abs((rrb-rra)/vxv**0.5);
 Pvalue=probnorm(Zsc)*2;

 output;


 %if &all.=1 %then %do;
     keep diff rdx rdlx rdl rra rrb vxv ra: rb: rdl dmin;
 %end;
 %else %do;
     keep rdl rdu dmin lmin Zsc Pvalue rankdiff ;
 %end;
run;

%end;

data &out.;
 set dat2;
 RateDiff=rankdiff;
 LowerCL=rdl;
 UpperCL=rdu;
 Z=Zsc;
 P=Pvalue;
 keep ratediff LowerCL UpperCL Z P;
run;

%mend;


/*dat:输入的数据集文件名,stra:分层因素,trt:分组变量名,c:事件数的变量名,n:样本量的变量名,每一层的n都不能为0*/
/*Wmethod为权重计算方法,本次分析使用Wmethod=2*/
/*Wmethod=1: Wi=n0i+n1i*/
/*Wmethod=2: Wi=1/(1/n0i+1/n1i)*/

/*当stra设为0时,为不分层的MN方法*/

SAS实现COX比例风险回归模型
关于phreg中参考值的选择， 你可以在程序中加ref, 比如下面的例子
ods output ParameterEstimates=HarzedRatio;
proc phreg data = adtte;
  class trtn(ref='1');
  model aval * CNSR(1) = trtn / risklimits ties = exact alpha=0.05;
  strata strata1 strata2 strata3; *** 分层因素 ***;
run;

Cox 模型中基于殃残差的比例风险判定
Cox 模型中的比例风险假定判定的方法和软件操作，推荐 UCLA 的 IDRE 官方网页：
https://stats.idre.ucla.edu/other/examples/asa2/testing-the-proportional-hazard-assumption-in-cox-models/
Cox 模型中基于殃残差的比例风险判定，在SAS的 phreg 过程步中有一个非常方便的实现方法，那就是经常被大家忽略的 assess 语句。

proc phreg data=sashelp.bmt;
  class group;
     model T * Status(0)=group /rl ;
  assess ph;
run;

基于power model评价PK参数
\(ln(y)=\alpha+\beta*ln(dose)\)，其中\(y\)表示PK参数如\(AUC_{0-inf}\),\(C_{max}\)等，\(\alpha\) 代表截距项，\(\beta\) 代表斜率。
MIX model计算P值。
data pp1;
    set adpcp.adpp;
    where PKPSFL="Y" AND paramcd="CMAX" AND PPGRPID="DAY 1, DOSE 1";
    logaval=log(aval);
    logdose=log(trtan);
run;

proc mixed data=pp1;
    class subjid ;
    model logaval=logdose/ddfm=kr;
    random intercept logdose/subject= subjid type=un gcorr s;
/*  lsmeans trtan / diff=control('R') alpha=0.1 cl;*/
    estimate 'aa' logdose 1/ cl alpha=0.1;
quit;
参考文章计算置信区间与判断区间。 置信区间法用于线性药代动力学特征评价
data pkdata1;
    set pkdata;
    logaval=log(aval);
    logdose=log(dose);
run;

proc means data=pkdata1;
    by dose;
    var aval;
run;

proc reg data=pkdata1;
    model aval=dose;
quit;

**** 计算回归方程的斜率及其置信区间 ****;
proc reg data=pkdata1;
    model logaval=logdose/ clb alpha=0.1;
quit;


**** 判断区间 ****;
data ecl;
    le=0.8;
    ue=1.25;
    r=2.5/0.625;
    lcl=1+log(0.8)/log(r);
    ucl=1+log(1.25)/log(r);
run;

均值求置信区间CI（连续性统计指标）
proc means data=sashelp.class mean clm;
    var age;
run;

率（百分比）指标求置信区间CI
proc freq data=sashelp.class; 
   table sex/binomial(all);
run;
如果是列联表，基于Clopper-Pearson exact method计算响应的受试者比例及其95%CI
ods output  BinomialCLs=binomial;
proc freq data=ci1 ;
  by paramn avisitn trtan;
  tables seq/ binomial(clopperpearson);
  weight freq/zeros;
run;

** seq=0/1, 表示是否触发疗效相应 **

独立两样本t检验，计算2组间的均值的差值和差值的95% CI
PROC TTEST DATA=ADEFF CI=EQUAL;
    CLASS TRT01PN;
    VAR PCHG;
RUN;

协方差分析
基线PASI评分（base）作为协变量，治疗分组（trt01an）和中心组别（siteid）作为固定效应，并考虑治疗分组与中心组别的交互效应
proc glm data=adeff;
    class trt01pn siteid;
    model pchg = base trt01pn siteid trt01pn*siteid ;
    estimate "a-b" trt01pn 1-1;
    lsmeans trt01pn/stderr tdiff pdiff cl ;
run;
协方差分析（ANOCVA）对48周BCVA较基线改善的平均字母数
ods output lsmeans=lsmean tests3=tests  diffs=diffs; 
proc mixed data=DATASET(where=(trtan in (2,1)));
  by &subgrp paramn avisitn ;
  class trtan(ref='1') &stra;
  model CHG=BASE TRTAN PCV WAMD;
  lsmeans trtan / cl pdiff; 
run;
** lsmeans, BCVA 第48 周的相对基线变化的最小二乘均值（LS Mean）以及对应的95%置信区间 **
** diffs, 两组的最小二乘均值差（LS difference）以及相应的95%的置信区
** tests3, 两组的双侧检验的P 值

adjusted P value（校正后的P值）
校正后的P值是对P值进行了多重假设检验，能更多地控制假阳性率。
proc multtest inpvalues=a holm hoc fdr;
run;
